SELECT `curso_usu`.`CursoID`, curso.CursoID, curso.Nombre, curso.Descripci√≥n, curso.Fecha_Ini, curso.Fecha_Fin, usuario.UsuarioID, usuario.Nombre, usuario.Apellido_P, usuario.Apellido_M, instructor.InstructorID, instructor.Nombre, instructor.Apellido_P, instructor.Apellido_M from curso_usu JOIN curso on curso_usu.CursoID = curso.CursoID join usuario on curso_usu.UsuarioID = usuario.UsuarioID join instructor on curso.InstructorID = instructor.InstructorID where curso_usu.UsuarioID = 1;